CREATE TABLE "CUSTOMER_REVIEWS" (
    "REVIEW_ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE,
    "CUSTOMER_ID" NUMBER NOT NULL ENABLE,
    "RATING" NUMBER(1, 0),
    "COMMENT_TEXT" VARCHAR2(500),
    "REVIEW_DATE" DATE DEFAULT sysdate,
    CHECK (RATING BETWEEN 1 AND 5) ENABLE,
    PRIMARY KEY ("REVIEW_ID") USING INDEX ENABLE
);
