CREATE TABLE "CUSTOMERS" (
    "CUSTOMER_ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE,
    "FIRST_NAME" VARCHAR2(50) NOT NULL ENABLE,
    "LAST_NAME" VARCHAR2(50) NOT NULL ENABLE,
    "PHONE" VARCHAR2(15) NOT NULL ENABLE,
    "EMAIL" VARCHAR2(100),
    "ADDRESS" VARCHAR2(200),
    "CREATION_DATE" DATE DEFAULT sysdate,
    PRIMARY KEY ("CUSTOMER_ID") USING INDEX ENABLE,
    UNIQUE ("PHONE") USING INDEX ENABLE
);
