CREATE TABLE "INVENTORY" (
    "PART_ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE,
    "PART_NAME" VARCHAR2(100) NOT NULL ENABLE,
    "QUANTITY" NUMBER(5, 0) NOT NULL ENABLE,
    "PRICE" NUMBER(10, 2) NOT NULL ENABLE,
    "SUPPLIER_ID" NUMBER,
    PRIMARY KEY ("PART_ID") USING INDEX ENABLE
);
