CREATE TABLE "PROMOTIONS" (
    "PROMOTION_ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE,
    "NAME" VARCHAR2(100) NOT NULL ENABLE,
    "DESCRIPTION" VARCHAR2(500),
    "START_DATE" DATE NOT NULL ENABLE,
    "END_DATE" DATE NOT NULL ENABLE,
    "DISCOUNT_PERCENT" NUMBER(5, 2),
    "SERVICE_ID" NUMBER,
    PRIMARY KEY ("PROMOTION_ID") USING INDEX ENABLE
);
