CREATE TABLE "SERVICES" (
    "SERVICE_ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE,
    "SERVICE_NAME" VARCHAR2(100) NOT NULL ENABLE,
    "DESCRIPTION" VARCHAR2(500),
    "PRICE" NUMBER(10, 2) NOT NULL ENABLE,
    PRIMARY KEY ("SERVICE_ID") USING INDEX ENABLE
);
