CREATE TABLE "STATISTICAL_REPORTS" (
    "REPORT_ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE,
    "CUSTOMER_ID" NUMBER NOT NULL ENABLE,
    "REPAIR_COUNT" NUMBER,
    "TOTAL_AMOUNT" NUMBER(10, 2),
    "MOST_FREQUENT_SERVICE" VARCHAR2(100),
    "MOST_FREQUENT_PART" VARCHAR2(100),
    "LAST_ORDER_ID" NUMBER,
    "LAST_DATE" DATE,
    "CREATION_DATE" DATE DEFAULT SYSDATE,
    PRIMARY KEY ("REPORT_ID") USING INDEX ENABLE
);
