CREATE TABLE "SUPPLIERS" (
    "SUPPLIER_ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE,
    "NAME" VARCHAR2(100) NOT NULL ENABLE,
    "PHONE" VARCHAR2(15),
    "EMAIL" VARCHAR2(100),
    "ADDRESS" VARCHAR2(200),
    PRIMARY KEY ("SUPPLIER_ID") USING INDEX ENABLE
);
