CREATE TABLE "SUPPLY_ORDER_DETAILS" (
    "DETAIL_ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE,
    "SUPPLY_ORDER_ID" NUMBER NOT NULL ENABLE,
    "PART_ID" NUMBER NOT NULL ENABLE,
    "QUANTITY" NUMBER(5, 0),
    "PRICE" NUMBER(10, 2),
    PRIMARY KEY ("DETAIL_ID") USING INDEX ENABLE
);
