CREATE TABLE "SUPPLY_ORDERS" (
    "SUPPLY_ORDER_ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE,
    "SUPPLIER_ID" NUMBER NOT NULL ENABLE,
    "ORDER_DATE" DATE DEFAULT sysdate,
    "STATUS" VARCHAR2(50) DEFAULT 'Pending', -- 'Pending', 'Fulfilled'
    "SUPPLIER_NAME" VARCHAR2(100),
    PRIMARY KEY ("SUPPLY_ORDER_ID") USING INDEX ENABLE
);
