CREATE TABLE "VEHICLES" (
    "VEHICLE_ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE,
    "CUSTOMER_ID" NUMBER NOT NULL ENABLE,
    "REGISTRATION_NUMBER" VARCHAR2(20) NOT NULL ENABLE,
    "MAKE" VARCHAR2(50) NOT NULL ENABLE,
    "MODEL" VARCHAR2(50) NOT NULL ENABLE,
    "PRODUCTION_YEAR" NUMBER(4, 0),
    "VIN" VARCHAR2(17) NOT NULL ENABLE,
    "ADD_DATE" DATE DEFAULT sysdate,
    PRIMARY KEY ("VEHICLE_ID") USING INDEX ENABLE,
    UNIQUE ("REGISTRATION_NUMBER") USING INDEX ENABLE,
    UNIQUE ("VIN") USING INDEX ENABLE
);
